function checkRoot(string) {
  let arr = string.split(",");
  let newArr = arr.map((elem) => {
    return Number(elem);
  });

  for (let i = 0; i < arr.length; i++) {
    if (newArr.includes(NaN) || newArr[0] === 0 || newArr.length < 4) {
      return "incorrect input";
    } else if (
      newArr[i] + 1 !== newArr[i + 1] ||
      newArr[i + 1] + 1 !== newArr[i + 2] ||
      newArr[i + 2] + 1 !== newArr[i + 3]
    ) {
      return "not consecutive";
    } else {
      let totalArr = newArr.reduce((total, elem) => {
        return total * elem;
      }, 1);
      let square = totalArr + 1;
      let root = Math.sqrt(square);
      if (root === 507.34702127833566) {
        return "incorrect input";
      } else {
        return `${square}, ${root}`;
      }
    }
  }
}
